def compter_handles(handles)
    nombre_de_handles = handles.length #Array.length() : length() is a Array class method which returns the number of elements in the array.
    puts "Il y a #{nombre_de_handles} handles dans cette array."
  end
  

  def handle_le_plus_court(handles)
     handle_le_plus_court = handles.min_by { |handle| handle.length } # .min_by retourne l'élément ayant la plus petite valeur .
    puts "Le handle le plus court est : #{handle_le_plus_court}"
  end
   

  def nombre_handles_avec_5_caracteres(handles)
    
    nombre_handles = handles.count { |handle| handle.length - 1 == 5 } # -1 pour exclure le caractère "@"
    puts "Il y a #{nombre_handles} handles avec 5 caractères dans cette array."
  end
  


  def nombre_handles_majuscule(handles)
    # Utilisation de la méthode count avec une expression Regexp pour compter les handles commençant par une majuscule
    nombre_handles = handles.count { |handle| handle =~ /@([A-Z])\w+/ }
    puts "Il y a #{nombre_handles} handles commençant par une majuscule dans cette array."
  end
  
  

  def trier_handles_par_ordre_alphabetique(handles)
   handles_tries_alphabet = handles.sort #".sort" trie par ordre alphabétique
    puts "Liste de handles triée par ordre alphabétique :"
    print handles_tries_alphabet
  end
  
  
  def trier_handles_par_taille(handles)

    handles_tries = handles.sort_by { |handle| handle.length }
    puts "Liste de handles triée par taille (du plus petit au plus grand) :"
    print handles_tries
  end
  

  def trouver_position_dans_array(handle_recherche, handles)
    position = handles.index(handle_recherche) #.index sert à retrouver position dans array
    
      puts "La position de #{handle_recherche} dans l'array est : #{position + 1}"  #+1 car commence de 0
    

    end
 
  

  def repartition_par_taille(handles)
    repartition = Hash.new(0)
  
    # Compter le nombre de handles pour chaque taille
    handles.each { |handle| repartition[handle.length - 1] += 1 }  # -1 pour exclure le caractère "@"
  
    puts "Répartition des handles par taille :"
    repartition.each do |taille, nombre|
      puts "#{taille + 1} caractère: #{nombre} handle"
    end
  end
  
  

  def perform(handles)
    compter_handles(handles)
    puts "\n"
    handle_le_plus_court(handles)
    puts "\n"
    nombre_handles_avec_5_caracteres(handles)
    puts "\n"
    nombre_handles_majuscule(handles)
    puts "\n"
    trier_handles_par_ordre_alphabetique(handles)
    puts "\n"
    trier_handles_par_taille(handles)
    puts "\n"
    trouver_position_dans_array("@epenser", handles)
    puts "\n"
    repartition_par_taille(handles)
    puts "\n"
  end

  perform(handles)